import { Component } from 'react';
import PropTypes from 'prop-types';
import pokemons from './data';

class Pokemon extends Component {
  render() {
    return (
      pokemons.map((pokemon) =>
        <div key={pokemon.id}>
        <ul className='card'>
          <li>Nome do pokemon: {pokemon.name}</li>
          <li>Tipo: {pokemon.type}</li>
          <li>Peso: {pokemon.averageWeight.value}.{pokemon.averageWeight.measurementUnit}</li>
          <li><img src={pokemon.image} alt={pokemon.name} /></li>
        </ul>
        </div>
      )
    );
  }
}

Pokemon.propTypes = {
  name: PropTypes.string,
  type: PropTypes.string,
  // averageWeight['value']: propTypes.number 
}

export default Pokemon;
